import React , {Component} from 'react';
import { Table,Button, ButtonGroup } from 'reactstrap';
import {CSSTransition, TransitionGroup } from 'react-transition-group';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import uuid from 'uuid';
import './product.css'

const styles = {
    transition:'opacity 3s ease',
    backgroundColor:'red'
};
class Product extends Component{
    state ={
        product:[
            {
                 id:uuid(),
                 productCount:1,
                 image:'abcd image',
                 name:'Pond Honey Lotion',
                 description:'Honey Lotion 500 ml bottle',
                 price:'500',
                 quantity:'20',
                 date:Date.now(),
                 status:'active'
             },
             {
                id:uuid(),
                productCount:1,
                image:'abcd image',
                name:'Loreal Red Lipstick',
                description:'Red Lipstick',
                price:'500',
                quantity:'20',
                date:Date.now(),
                status:'active'
            },
            // {
            //     // id:'',
            //     // productCount:0,
            //     // image:'',
            //     name:'',
            //     // description:'',
            //     // price:'',
            //     // quantity:'',
            //     // date:'',
            //     // status:''
            //  }

      ],

         opacity:1
    }

    handleAdd = e =>{
        const image = prompt('Enter Product Iame');
        const name = prompt('Enter Product Name');
        const description = prompt('Enter Product Description');
        const price = prompt('Enter Product Price');
        const quantity = prompt('Enter Product Quantity');
        const status = prompt('Enter Product Status');
        
        if(name && image && description && price && quantity  && status) {
            this.setState( state =>({
                product: [...state.product,{id:uuid(), name, image, description, price , date:Date.now(), quantity, status}]
            }) 
            )
            console.log(this.state.product.id)
        }
    }

    handleRemove = (product, id) =>{
        this.setState(state => ({
             opacity:0,
            product: state.product.filter(product => product.id !==id)
            
        }));
       
    }
    render(){
        const { product } = this.state;
         let productCount = 1;
         let animation = 'default';
        return(
            <div>
                <div className="row mb-3">
                    <div className="col-sm-10">
                        <h3 className="h3">Product Details</h3>
                    </div>
                    <div className="col-sm-2 " style={{ right:"0", padding:0}}>
                        <Button style={{backgroundColor:'#32bcdb'}}
                          onClick={this.handleAdd}  
                        > Add Product </Button>
                    </div>
                </div>
                
             <Table hover bordered responsive>
                <thead>
                    <tr>
                    <th>#</th>
                    {/* <th>id</th> */}
                    <th>Image</th>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                   
                    {product.map(({id, name , image , description, price, quantity, date , status }) => (
                         <tr key={id}>
                        {/* <tr key={id}> */}
                            <th scope="row"  stlye={{...styles, opacity:this.state.opacity}}>{productCount++}</th>
                            {/* <td>{id}</td> */}
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{image}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{name}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{description}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{price}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{quantity}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{date}</td>
                            <td  stlye={{...styles, opacity:this.state.opacity}}>{status}</td>
                            <td align="center">
                                <ButtonGroup size="md">
                                    <Button style={{backgroundColor:"#e39e44"}}>Edit</Button>
                                    <Button style={{backgroundColor:"#c22370"}} 
                                    onClick={() =>this.handleRemove(product,id)}>Delete</Button>
                                </ButtonGroup>
                            </td>
                        </tr>
                    
                    ))}
                    
                </tbody>
            </Table>
            </div>
        );
    }
}
export default Product;
