import React, { Component} from 'react'
import DrawerToggleButton from '../SideDrawer/DrawerToggleButton'
import './toolbar.css'

class Toolbar extends Component{
    state = {
        slideLogo:false
    }

    handleChildClick = ()=>{
        this.setState((prevState)=> {
            slideLogo= !prevState.slideLogo
        });
    }
    render(){
        let toolbarLogo = 'toolbar_logo';
        if(this.state.slideLogo){
            toolbarLogo = 'toolbar-logo slide';
        }
        return(
            <header className="toolbar">
            <nav className="toolbar_navigation" >
                <div>
                    <DrawerToggleButton click={props.drawerClickHandler} onChildClick={handleChildClick}/>
                </div>
                <div className={tool}>
                    <a href="/" >MAKEUP TRACEBOX</a>
                </div>
                <div className="spacer"></div>
                <div>
                    <ul>
                        <li className="toolbar_navigation-items"><a href="/">Setting</a></li>
                        <li className="toolbar_navigation-items"><a href="/">Logout</a></li>
                    </ul>
                </div>
            </nav>
        </header>
        );
    }
}


 export default Toolbar
