import React , {Component} from 'react';
import { Table,Button, ButtonGroup } from 'reactstrap';
import {CSSTransition, TransitionGroup } from 'react-transition-group';
import uuid from 'uuid';
import './product.css'
class Product extends Component{
    state ={
        product:[
            {// id:'',
            // productCount:0,
            // image:'',
             name:'',
            // description:'',
            // price:'',
            // quantity:'',
            // date:'',
            // status:''
        }

      ]
    }

    render(){
        const { product } = this.state;
        return(
            <div>
                <div className="row mb-3">
                    <div className="col-sm-10">
                        <h3 className="h3">Product Details</h3>
                    </div>
                    <div className="col-sm-2 " style={{ right:"0", padding:0}}>
                        <Button style={{backgroundColor:'#32bcdb'}}
                          onClick={() =>{
                              const name = prompt('Enter Product Name');
                              if(name) {
                                  this.setState( state =>({
                                      product: [...state.product,{name}]
                                  }) 
                                  )
                                  console.log(this.state.product)
                              }
                          }}  
                        > Add Product </Button>
                    </div>
                </div>
                
             <Table hover bordered>
                <thead>
                    <tr>
                    <th>#</th>
                    <th>Image</th>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <TransitionGroup className="product-table">
                          {product.map({})}
                    </TransitionGroup>
                    <tr>
                    <th scope="row">{product.productCount}</th>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                    <td>Mark</td>
                    <td align="center">
                        <ButtonGroup size="md">
                            <Button style={{backgroundColor:"#e39e44"}}>Edit</Button>
                            <Button style={{backgroundColor:"#c22370"}}>Delete</Button>
                        </ButtonGroup>
                    </td>
                    </tr>
                    <tr>
                    <th scope="row">2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <th scope="row">3</th>
                    <td>Larry</td>
                    <td>the Bird</td>
                    <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
            </div>
        );
    }
}
export default Product;
