import React , {Component} from 'react';
import { Table,Button, ButtonGroup } from 'reactstrap';
import {CSSTransition, TransitionGroup } from 'react-transition-group';
import uuid from 'uuid';
import './product.css'
class Product extends Component{
    state ={
        product:[
            {
                 id:uuid(),
                 productCount:1,
                 image:'abcd image',
                 name:'Pond Honey Lotion',
                 description:'Honey Lotion 500 ml bottle',
                 price:'500',
                 quantity:'20',
                 date:Date.now(),
                 status:'active'
             },
             {
                id:uuid(),
                productCount:1,
                image:'abcd image',
                name:'Loreal Red Lipstick',
                description:'Red Lipstick',
                price:'500',
                quantity:'20',
                date:Date.now(),
                status:'active'
            },
            {
                // id:'',
                // productCount:0,
                // image:'',
                name:'',
                // description:'',
                // price:'',
                // quantity:'',
                // date:'',
                // status:''
             }

      ]
    }

    render(){
        const { product } = this.state;
        return(
            <div>
                <div className="row mb-3">
                    <div className="col-sm-10">
                        <h3 className="h3">Product Details</h3>
                    </div>
                    <div className="col-sm-2 " style={{ right:"0", padding:0}}>
                        <Button style={{backgroundColor:'#32bcdb'}}
                          onClick={() =>{
                              const image = prompt('Enter Product Iame');
                              const name = prompt('Enter Product Name');
                              const description = prompt('Enter Product Description');
                              const price = prompt('Enter Product Price');
                              const quantity = prompt('Enter Product Quantity');
                              const status = prompt('Enter Product Status');
                              if(name && image && description && price && quantity && date && status) {
                                  this.setState( state =>({
                                      product: [...state.product,{name, image, description, price , qunatity, date,status}]
                                  }) 
                                  )
                                  console.log(this.state.product)
                              }
                          }}  
                        > Add Product </Button>
                    </div>
                </div>
                
             <Table hover bordered>
                <thead>
                    <tr>
                    <th>#</th>
                    <th>Image</th>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <TransitionGroup className="product-table">
                          {product.map(({id, name , image , descrpition, price, qunatity, date , status }) => (
                              <CSSTransition key={id} timeout={500} className="fade">
                                <tr>
                                    <th scope="row">{product.productCount}</th>
                                    <td>Mark</td>
                                    <td>Otto</td>
                                    <td>@mdo</td>
                                    <td>Mark</td>
                                    <td>Otto</td>
                                    <td>@mdo</td>
                                    <td>Mark</td>
                                    <td align="center">
                                        <ButtonGroup size="md">
                                            <Button style={{backgroundColor:"#e39e44"}}>Edit</Button>
                                            <Button style={{backgroundColor:"#c22370"}}>Delete</Button>
                                        </ButtonGroup>
                                    </td>
                                </tr>
                              </CSSTransition>
                          ))}
                    </TransitionGroup>
                  
                    <tr>
                    <th scope="row">2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                    </tr>
                    <tr>
                    <th scope="row">3</th>
                    <td>Larry</td>
                    <td>the Bird</td>
                    <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
            </div>
        );
    }
}
export default Product;
